import React, { Component } from "react";

import Burger from "../../components/Burger";
import BuildControls from "../../components/BuildControls"

class BurgerBuilder extends Component {
  state = {
    ingredients: {
      salad: 0,
      cheese: 0,
      bacon: 0,
      meat: 0
    }
  };

  // class base object-ийн дотоод "state"-ийг өөрчлөхдөө заавалчгүй шинэ object байгуулах шаардлагатай, эс бөгөөс рендер хийгдэх үед ямар нэгэн хуучин state-тэйгээ адилхан гэж үзээд рендер хийдэггүй.
ortsNemeh = (type) => {this.setState()
}

  render() {
    return (
      <div>
        <Burger orts={this.state.ingredients} />
        <BuildControls ortsNemeh={this.ortsNemeh}/>
   
        
      </div>
    );
  }
}

export default BurgerBuilder;

