import React, { Component } from "react";

import Burger from "../../components/Burger";
import BuildControls from "../../components/BuildControls";

const INGREDIENT_PRICES = { salad: 150, cheese: 250, bacon: 800, meat: 1500 };

class BurgerPage extends Component {
  state = {
    ingredients: {
      salad: 0,
      cheese: 0,
      bacon: 0,
      meat: 0,
    },

    totalPrice: 1000,
    purchasing: false,
  };

  // class base object-ийн дотоод "state"-ийг өөрчлөхдөө заавалчгүй шинэ object байгуулах шаардлагатай, эс бөгөөс рендер хийгдэх үед ямар нэгэн хуучин state-тэйгээ адилхан гэж үзээд рендер хийдэггүй. Тиймээс эхлээд object-ийн хуулбарыг үүёгэх шаардлагатай, хамгийн амархан арга нь байгуулагч функц ашиглах юм.
  ortsNemeh = (type) => {
    const newIngredients = { ...this.state.ingredients };
    newIngredients[type]++;

    const newPrice = this.state.totalPrice + INGREDIENT_PRICES[type];

    this.setState({ totalPrice: newPrice, ingredients: newIngredients });
  };

  //орцоо хасахын тулд ingredients нь 0 байж болохгүй учир эхлээд нөхцөл шалгана.
  ortsHasah = (type) => {
    if (this.state.ingredients[type] > 0) {
      const newIngredients = { ...this.state.ingredients };
      newIngredients[type]--;

      const newPrice = this.state.totalPrice - INGREDIENT_PRICES[type];

      this.setState({ totalPrice: newPrice, ingredients: newIngredients });
    }
  };

  render() {
    const disabledIngredients = { ...this.state.ingredients };
    for (let key in disabledIngredients) {
      disabledIngredients[key] = disabledIngredients[key] <= 0;
    }
    return (
      <div>
        <Burger orts={this.state.ingredients} />
        <BuildControls
          disabled={!this.state.purchashing}
          price={this.state.totalPrice}
          disabledIngredients={disabledIngredients}
          ortsHasah={this.ortsHasah}
          ortsNemeh={this.ortsNemeh}
        />
      </div>
    );
  }
}

export default BurgerPage;

