import React from "react";
import BuildControl from "../BuildControl";
import css from "./style.module.css";


const BuildControls = props => {
  
  const controls = {bacon: "Гахайн мах", cheese: "Бяслаг", meat:'Үхрийн мах', salad: "Салад"}
  
  return
  (
    <div className={css.BuildControls}>
      <p> Бургерийн үнэ: <strong>{props.price}</strong> </p>
      <BuildControl disabled={props.disabledIngredients} ortsHasah={props.ortsHasah} ortsNemeh={props.ortsNemeh} type="salad" orts="Салад" />
      <BuildControl disabled={props.disabledIngredients} ortsHasah={props.ortsHasah} ortsNemeh={props.ortsNemeh} type="bacon" orts="Гахайн мах" />
      <BuildControl disabled={props.disabledIngredients} ortsHasah={props.ortsHasah} ortsNemeh={props.ortsNemeh} type="cheese" orts="Бяслаг" />
      <BuildControl disabled={props.disabledIngredients} ortsHasah={props.ortsHasah} ortsNemeh={props.ortsNemeh} type="meat" orts="Үхрийн мах" />
    </div>
  );}

export default BuildControls;

// <BuildControl ortsNemeh={props.ortsNemeh} type="salad" orts="Салад" /> гэдэг нь BurgerBuilder-аас ortsNemeh гэдгийг props-оор нь дамжуулан авч байна гэсэн үг.
